let readline = require ("readline-sync");

let numero;
let maximo = -9007199254740991
let minimo = 9007199254740991
let suma = 0
let contador = 0
numero = readline.questionInt ("Ingrese un numero: ");

if (numero == 0){
    console.log ("Usted no infreso ningun numero.")
}else{
    while (numero !=0){
        suma = numero + suma;
        contador = contador + 1;
        
        if (numero>maximo){
            maximo = numero
        }
        if(numero<minimo){
            minimo=numero
        }
    numero = readline.questionInt ("Ingrese otro numero: ");
}

console.log ("El numero maximo es: ", maximo);
console.log ("El promedio es: ", suma/(contador) );
console.log ("El numero minimo es: ", minimo);
}